CREATE TABLE BUS (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), DRIVER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE TRAIN (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), DRIVER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE VENTRA (ID BIGINT AUTO_INCREMENT NOT NULL, BALANCE DOUBLE, CARDNUMBER VARCHAR(255), EXPIRYDATE DATE, LASTUPDATED DATETIME, PRIMARY KEY (ID))
CREATE TABLE STATION (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE DRIVER (ID BIGINT AUTO_INCREMENT NOT NULL, BIRTHDATE DATE, DRIVERBATCH VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), LASTUPDATED DATETIME, PRIMARY KEY (ID))
CREATE TABLE PASSENGER (ID BIGINT AUTO_INCREMENT NOT NULL, BIRTHDATE DATE, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), LASTUPDATED DATETIME, BUS_ID BIGINT, TRAIN_ID BIGINT, VENTRA_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE bus_stations (bus_id BIGINT NOT NULL, station_id BIGINT NOT NULL, PRIMARY KEY (bus_id, station_id))
CREATE TABLE train_stations (train_id BIGINT NOT NULL, station_id BIGINT NOT NULL, PRIMARY KEY (train_id, station_id))
ALTER TABLE BUS ADD CONSTRAINT FK_BUS_DRIVER_ID FOREIGN KEY (DRIVER_ID) REFERENCES DRIVER (ID)
ALTER TABLE TRAIN ADD CONSTRAINT FK_TRAIN_DRIVER_ID FOREIGN KEY (DRIVER_ID) REFERENCES DRIVER (ID)
ALTER TABLE PASSENGER ADD CONSTRAINT FK_PASSENGER_VENTRA_ID FOREIGN KEY (VENTRA_ID) REFERENCES VENTRA (ID)
ALTER TABLE PASSENGER ADD CONSTRAINT FK_PASSENGER_BUS_ID FOREIGN KEY (BUS_ID) REFERENCES BUS (ID)
ALTER TABLE PASSENGER ADD CONSTRAINT FK_PASSENGER_TRAIN_ID FOREIGN KEY (TRAIN_ID) REFERENCES TRAIN (ID)
ALTER TABLE bus_stations ADD CONSTRAINT FK_bus_stations_station_id FOREIGN KEY (station_id) REFERENCES STATION (ID)
ALTER TABLE bus_stations ADD CONSTRAINT FK_bus_stations_bus_id FOREIGN KEY (bus_id) REFERENCES BUS (ID)
ALTER TABLE train_stations ADD CONSTRAINT FK_train_stations_train_id FOREIGN KEY (train_id) REFERENCES TRAIN (ID)
ALTER TABLE train_stations ADD CONSTRAINT FK_train_stations_station_id FOREIGN KEY (station_id) REFERENCES STATION (ID)
