CREATE TABLE BUS (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE TRAIN (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE VENTRA (ID BIGINT AUTO_INCREMENT NOT NULL, BALANCE DOUBLE, CARDNUMBER VARCHAR(255), EXPIRYDATE DATE, LASTUPDATED DATETIME, PRIMARY KEY (ID))
CREATE TABLE STATION (ID BIGINT AUTO_INCREMENT NOT NULL, LASTUPDATED DATETIME, NAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE DRIVER (ID BIGINT AUTO_INCREMENT NOT NULL, BIRTHDATE DATE, DRIVERBATCH VARCHAR(255), FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), LASTUPDATED DATETIME, PRIMARY KEY (ID))
CREATE TABLE PASSENGER (ID BIGINT AUTO_INCREMENT NOT NULL, BIRTHDATE DATE, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), LASTUPDATED DATETIME, BUS_ID BIGINT, PRIMARY KEY (ID))
ALTER TABLE PASSENGER ADD CONSTRAINT FK_PASSENGER_BUS_ID FOREIGN KEY (BUS_ID) REFERENCES BUS (ID)
